    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        double a=0;
        a=Double.parseDouble(jTextField1.getText());
        a=a*Double.parseDouble(jTextField2.getText());
        
        
        jLabel6.setText("$"+a);
        jLabel7.setText("$"+a*((Double.parseDouble((String) jComboBox1.getSelectedItem()))/100));
        jLabel9.setText("$"+ (a-(a*((Double.parseDouble((String) jComboBox1.getSelectedItem()))/100))));
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

